$(function moreContent (){
	$(".blog-link").click(function moreContent() {
       $(".panel").slideToggle("slow")
   } );
} );

$(function hideLink (){
	$(".blog-link").click(function hideLink() {
       $(this).hide()
   } );
} );

$(function showLink (){
	$(".blog-link-less").click(function showLink() {
       $(this).hide()
   } );
} );

